mapper.select:
SELECT * FROM BOOKS
WHERE 1 = 1
AND ID = #{id}

mapper.insert:
INSERT INTO BOOKS
	( TITLE
	, PRICE
	, AUTHOR
	, DESCRIPTION
	, PUBLISHER
	, CATEGORY
	, RELEASE_DATE
	, CONDITIONS
	, IMAGE
	)
VALUES (
	  #{title}
	, #{price}
	, #{author}
	, #{description}
	, #{publisher}
	, #{category}
	, #{releaseDate}
	, #{conditions}
	, #{image}
	)

mapper.update:
UPDATE BOOKS
SET
	  TITLE = #{title}
	, PRICE = #{price}
	, AUTHOR = #{author}
	, DESCRIPTION = #{description}
	, PUBLISHER = #{publisher}
	, CATEGORY = #{category}
	, RELEASE_DATE = #{releaseDate}
	, CONDITIONS = #{conditions}
	, IMAGE = #{image}
WHERE
	ID = #{id}
