mapper.select:
SELECT * FROM BOOKS
WHERE 1 = 1
AND ID = #{id}

SELECT * FROM REVIEWS
WHERE 1 = 1
AND BOOK_ID = #{bookId}

mapper.insert:
INSERT INTO REVIEWS
	( BOOK_ID
	, EMAIL
	, COMMENT
	, RATING
	, CREATE_DATE
	)
VALUES (
	  #{bookId}
	, #{email}
	, #{comment}
	, #{rating}
	, DATE_FORMAT(NOW(), '%Y/%m/%d %H:%i:%s')
	)

INSERT INTO BOOKS
	( TITLE
	, PRICE
	, AUTHOR
	, DESCRIPTION
	, PUBLISHER
	, CATEGORY
	, RELEASE_DATE
	, CONDITIONS
	, IMAGE
	)
VALUES (
	  #{title}
	, #{price}
	, #{author}
	, #{description}
	, #{publisher}
	, #{category}
	, #{releaseDate}
	, #{conditions}
	, #{image}
	)

mapper.update:
UPDATE BOOKS
SET
	  TITLE = #{title}
	, PRICE = #{price}
	, AUTHOR = #{author}
	, DESCRIPTION = #{description}
	, PUBLISHER = #{publisher}
	, CATEGORY = #{category}
	, RELEASE_DATE = #{releaseDate}
	, CONDITIONS = #{conditions}
	, IMAGE = #{image}
WHERE
	ID = #{id}
